// client/src/components/PropertyCards.js
import React, { useState } from 'react';
import SwipeableViews from 'react-swipeable-views';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const PropertyCards = ({ properties }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const navigate = useNavigate();

  const handleSaveProperty = async (property) => {
    try {
      await axios.post('/api/users/save-property', property); // Adjust the API route accordingly
      alert('Property saved!');
    } catch (error) {
      console.error('Error saving property:', error);
      alert('Failed to save property.');
    }
  };

  const handleSwipe = (index, direction) => {
    if (direction === 'right') {
      handleSaveProperty(properties[index]);
    }

    if (index === properties.length - 1) {
      navigate('/saved');
    } else {
      setCurrentIndex(index + 1);
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto' }}>
      <SwipeableViews
        index={currentIndex}
        onChangeIndex={(index, _, direction) => handleSwipe(index, direction)}
      >
        {properties.map((property, i) => (
          <div key={property._id} style={{ position: 'relative', overflow: 'hidden', borderRadius: '8px', marginBottom: '20px' }}>
            <img
              src={property.coverPhoto || 'https://media.apts247.info/c5/c56bb974e1654031a72e0f55847c3e4b/hero_shot/community/community-amenities.jpg'}
              alt={property.propertyName}
              style={{ width: '100%', height: '400px', objectFit: 'cover' }}
            />
            <div
              style={{
                position: 'absolute',
                bottom: '0',
                left: '0',
                width: '100%',
                color: 'white',
                backgroundColor: 'rgba(0, 0, 0, 0.6)',
                padding: '20px'
              }}
            >
              <h3>{property.propertyName}</h3>
              <p>{property.description}</p>
              <p>{property.bedrooms} Bedrooms, {property.bathrooms} Bathrooms</p>
            </div>
          </div>
        ))}
      </SwipeableViews>
    </div>
  );
};

export default PropertyCards;
